grep -P "\tmRNA\t" out.gff | awk 'BEGIN {FS=OFS="\t"} {split($9, a, /;/); coverage=""; identity=""; for(i=1; i<=length(a); i++) { if (a[i] ~ /^coverage=/) {split(a[i], c, /=/); coverage=c[2]} else if (a[i] ~ /^identity=/) {split(a[i], d, /=/); identity=d[2]}}; print $0 "\t" coverage "\t" identity}'| sort -k1,1 -k11,11nr| awk 'BEGIN {FS=OFS="\t"} {split($9, a, /;/); sub("Name=", "", a[2]); print $0, a[2]}'| sort -k12,12 -k11,11nr > gmap_stats.txt
